// [snowpack] add styles to the page (skip if no document exists)
if (typeof document !== 'undefined') {
  const code = ".select-box-container {\n  height: 30px;\n  border: 1px solid var(--mainGray);\n  width: 100%;\n  margin: 0;\n  padding: 0;\n}\n\n* {\n  box-sizing: border-box;\n}\n\n.drop-down-select{\n  position: relative;\n  height: 30px;\n  left:-150px;\n}\n.select-box-box {\n  width: 120px;\n  /* position: absolute; */\n}\n\n.select-box-selected-item {\n  display: inline-block;\n  height: 100%;\n  width: 100%;\n  margin-bottom: 2px;\n  vertical-align: middle;\n  \n}\n.select-box-selected-item input {\n  width: calc(100% - 30px);\n  height: 100%;\n  border: 0;\n  outline: none;\n}\n\n.select-box-items{\n  z-index:1000;\n  position: fixed;\n  width: 200px;\n  background-color: var(--canvas);\n  border: 1px solid var(--mainGray);\n\n  box-shadow: 0 2px 4px -1px var(--mainGray), 0 4px 5px 0 var(--mainGray), 0 1px 10px 0 var(--mainGray);\n}\n\n.select-box-items div {\n  border: none;\n  line-height: 2em;\n  height: 3em;\n  display: flex;\n  align-items: center;\n}\n\n.select-box-items .parent {\n  color: var(--canvastext);\n  padding: 0px 16px;\n}\n\n.child-container {\n  padding: 5px 16px;\n  cursor: pointer;\n}\n\n.child-container:hover {\n  background-color: var(--mainGray);\n}\n\n.child-icon {\n  width: 20px;\n  height: 20px;\n}\n\n.select-box-arrow {\n  width: 30px;\n  height: 30px;\n  margin: 0;\n  padding: 0;\n  display: inline-block;\n  background: var(--mainGray);\n  position: absolute;\n  right: 0;\n  top: 0;\n}\n\n.select-box-arrow-down {\n  position: absolute;\n  top: 10px;\n  left: 8px;\n  width: 0;\n  height: 0;\n  border-left: 8px solid transparent;\n  border-right: 8px solid transparent;\n  border-top: 10px solid var(--canvastext);\n}\n\n.select-box-arrow-up {\n  position: absolute;\n  top: 10px;\n  left: 8px;\n  width: 0;\n  height: 0;\n  border-left: 8px solid transparent;\n  border-right: 8px solid transparent;\n  border-bottom: 10px solid var(--canvastext);\n}\n\n";

  const styleEl = document.createElement("style");
  const codeEl = document.createTextNode(code);
  styleEl.type = 'text/css';
  styleEl.appendChild(codeEl);
  document.head.appendChild(styleEl);
}