// [snowpack] add styles to the page (skip if no document exists)
if (typeof document !== 'undefined') {
  const code = "/* Customize the label (the container) */\n.container {\n    /* display: inline-block; */\n    position: relative;\n    padding-left: 24px;\n    margin-bottom: 4px;\n    margin-right: 2px; /* 2px less than other components (6px marginRight) due to the 2px marginLeft on label */\n    cursor: pointer;\n    -webkit-user-select: none;\n    -moz-user-select: none;\n    -ms-user-select: none;\n    user-select: none;\n}\n\n/* .button-container {\n    display: inline-block;\n    position: relative;\n    margin-bottom: 12px;\n    margin-right: 2px;\n    cursor: pointer;\n    -webkit-user-select: none;\n    -moz-user-select: none;\n    -ms-user-select: none;\n    user-select: none;\n} */\n  \n/* Hide the browser's default checkbox */\n.container input {\n    position: absolute;\n    opacity: 0;\n    cursor: pointer;\n    height: 0;\n    width: 0;\n}\n  \n/* Create a custom checkbox */\n.checkmark {\n    position: absolute;\n    top: 0;\n    left: 0;\n    height: 18px;\n    width: 18px;\n    background-color: #eee;\n    border-style: solid;\n    border-color: #000;\n    border-radius: var(--mainBorderRadius);\n}\n  \n/* On mouse-over, add a light blue background color */\n.container:hover input ~ .checkmark {\n    background-color: var(--lightBlue);\n}\n  \n/* When the checkbox is checked, add a dark blue background */\n.container input:checked ~ .checkmark {\n    background-color: var(--mainBlue);\n}\n\n/* When a checkbox has focus, add a dark blue outline */\n.container:focus-within input ~ .checkmark {\n    outline: 2px solid black;\n    outline-offset: 2px;\n}\n  \n/* Create the checkmark/indicator (hidden when not checked) */\n.checkmark:after {\n    content: \"\";\n    position: absolute;\n    display: none;\n}\n  \n/* Show the checkmark when checked */\n.container input:checked ~ .checkmark:after {\n    display: block;\n}\n  \n/* Style the checkmark/indicator */\n.container .checkmark:after {\n    left: 5px;\n    top: 1px;\n    width: 5px;\n    height: 10px;\n    border: solid white;\n    border-width: 0 3px 3px 0;\n    -webkit-transform: rotate(45deg);\n    -ms-transform: rotate(45deg);\n    transform: rotate(45deg);\n}\n  ";

  const styleEl = document.createElement("style");
  const codeEl = document.createTextNode(code);
  styleEl.type = 'text/css';
  styleEl.appendChild(codeEl);
  document.head.appendChild(styleEl);
}